insert into "Genre" (genre_id, genre_name, genre_description, genre_image) values (1, 'Fantasy', 'Step into a world of magic and mythical creatures, where epic quests and heroic adventures await. Immerse yourself in richly imagined realms filled with wondrous beings and enchanted landscapes.', '/images/genres/fantasy.jpeg');
insert into "Genre" (genre_id, genre_name, genre_description, genre_image) values (2, 'Horror', 'Embrace the spine-chilling darkness of horror, where fear lurks in the shadows and malevolent entities prowl. From haunted houses to supernatural terrors, prepare to be terrified by the unknown.', '/images/genres/horror.jpeg');
insert into "Genre" (genre_id, genre_name, genre_description, genre_image) values (3, 'Nature and Science', 'Explore the wonders of the natural world and the mysteries of science. Delve into the intricacies of life, the universe, and everything in between, as you unravel the secrets of our existence.', '/images/genres/science.jpeg');
insert into "Genre" (genre_id, genre_name, genre_description, genre_image) values (4, 'Health and Wellness', 'Discover a path to well-being and a healthier lifestyle. From physical fitness to mental wellness, embark on a journey of self-improvement and holistic balance.', '/images/genres/health.jpeg');
insert into "Genre" (genre_id, genre_name, genre_description, genre_image) values (5, 'Romance', 'Indulge in the sweet and passionate tales of love and romance. Experience heartwarming connections, dramatic emotions, and love stories that will tug at your heartstrings.', '/images/genres/romance.jpeg');
insert into "Genre" (genre_id, genre_name, genre_description, genre_image) values (6, 'Science Fiction', 'Venture into the realms of the future and beyond, where technology and imagination merge. Explore futuristic worlds, space exploration, and encounters with extraterrestrial life.', '/images/genres/scifi.jpeg');
insert into "Genre" (genre_id, genre_name, genre_description, genre_image) values (7, 'True Crime', 'Delve into the dark and gripping world of true crime. Uncover the chilling details of real-life mysteries, investigations, and the minds of criminals.', '/images/genres/true_crime.jpeg');
insert into "Genre" (genre_id, genre_name, genre_description, genre_image) values (8, 'Philosophy', 'Engage in profound questions and intellectual exploration. Delight in the pursuit of knowledge, wisdom, and the complexities of human existence.', '/images/genres/philosophy.jpeg');
